package com.sira.learning.javalearningdec22.Gayathri;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Arithmetic Operators + - * / %
		
		int a=10;
		int b=15;
		int result;
		
		result=a+b;
		System.out.println("addition="+result);
		
		result=a-b;
		System.out.println("subtraction="+result);
		
		result=a*b;
		System.out.println("Multiplication="+result);
		
		result=a/b;
		System.out.println("Division="+a);
		
		result=a%b;
		System.out.println("Modulo="+result);
		
		//Increment Operator
		
		System.out.println("Value of a="+a);
		
		++a;
		System.out.println("Pre increment value="+a);
		
		a++;
		System.out.println("post increment value="+a);
		
		//Decrement Operator
		
		--a;
		System.out.println("Pre decrement value="+a);
		
		a--;
		System.out.println("Post decrement value="+a);
		
		
		
		
		

	}

}
